import React, { useState } from 'react';

function Home() {
  const [stations, setStations] = useState([
    {
      id: 1,
      name: 'Station A',
      address: '123 Main St, City A',
      status: 'Available',
      waitTime: 0,
      isFavorite: false,
    },
    {
      id: 2,
      name: 'Station B',
      address: '456 Elm St, City B',
      status: 'Occupied',
      waitTime: 3,
      isFavorite: false,
    },
  ]);

  // Toggle favorite status for a station
  const toggleFavorite = (id) => {
    setStations((prevStations) =>
      prevStations.map((station) =>
        station.id === id ? { ...station, isFavorite: !station.isFavorite } : station
      )
    );
  };

  // Open navigation to a station using Google Maps
  const navigateToStation = (address) => {
    const encodedAddress = encodeURIComponent(address);
    window.open(`https://www.google.com/maps/dir/?api=1&destination=${encodedAddress}`);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Charging Stations</h1>

      {/* Display list of stations */}
      <div>
        <h2>Available Stations</h2>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {stations.map((station) => (
            <li
              key={station.id}
              style={{
                margin: '10px 0',
                border: '1px solid #ccc',
                padding: '10px',
                borderRadius: '5px',
              }}
            >
              <h3>{station.name}</h3>
              <p><strong>Address:</strong> {station.address}</p>
              <p><strong>Status:</strong> {station.status}</p>
              <p><strong>Wait Time:</strong> {station.waitTime} people waiting</p>
              <button
                onClick={() => navigateToStation(station.address)}
                style={{
                  marginRight: '10px',
                  padding: '5px 10px',
                  cursor: 'pointer',
                  background: '#4CAF50',
                  color: 'white',
                  border: 'none',
                  borderRadius: '3px',
                }}
              >
                Navigate to Station
              </button>
              <button
                onClick={() => toggleFavorite(station.id)}
                style={{
                  padding: '5px 10px',
                  cursor: 'pointer',
                  background: station.isFavorite ? '#FF5722' : '#008CBA',
                  color: 'white',
                  border: 'none',
                  borderRadius: '3px',
                }}
              >
                {station.isFavorite ? 'Remove from Favorites' : 'Add to Favorites'}
              </button>
            </li>
          ))}
        </ul>
      </div>

      {/* Placeholder for search functionality */}
      <div style={{ marginTop: '20px' }}>
        <h2>Search for Stations</h2>
        <p>Search feature will be implemented here.</p>
      </div>
    </div>
  );
}

export default Home;
