import React, { useState } from 'react';
import { loginUser } from './api';
import { Link, useNavigate } from 'react-router-dom';
import './login.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const [loginError, setLoginError] = useState('');  // משתנה עבור הודעת שגיאה
  const navigate = useNavigate();

  const validateForm = () => {
    const newErrors = {};
    if (!email) {
      newErrors.email = 'Email is required.';
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      newErrors.email = 'Please enter a valid email address.';
    }
    if (!password) {
      newErrors.password = 'Password is required.';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    try {
      const data = await loginUser(email, password);
      localStorage.setItem('token', data.token);
      alert('Login successful');
      // אם ההתחברות הצליחה, נעבור לדף הבית
      navigate('/home');
    } catch (error) {
      // במקרה של שגיאה בהתחברות, נוודא שהמייל או הסיסמה לא נכונים
      if (error.response && error.response.status === 401) {
        setLoginError('Invalid email or password');  // הצגת הודעת שגיאה
      } else {
        setLoginError('An error occurred. Please try again later');  // שגיאה כלשהי אחרת
      }
    }
  };

  return (
    <div className="login-container">
      <form onSubmit={handleSubmit} noValidate>
        <h1>Welcome Back!</h1>
        
        {/* הצגת הודעת שגיאה אם קיימת */}
        {loginError && <div style={{ color: 'red', marginBottom: '10px' }}>{loginError}</div>}
        
        <div>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          {errors.email && <small style={{ color: 'red' }}>{errors.email}</small>}
        </div>
        <div>
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          {errors.password && <small style={{ color: 'red' }}>{errors.password}</small>}
        </div>
        <button type="submit">Login</button>
        <p>
          Not registered?{' '}
          <Link to="/register" className="register-link">
            Create an account
          </Link>
        </p>
      </form>
    </div>
  );
}

export default Login;
