import React, { useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

function Home() {
  const [stations, setStations] = useState([
    {
      id: 1,
      name: 'Station A',
      address: '123 Main St, City A',
      status: 'Available',
      waitTime: 0,
      isFavorite: false,
      location: { lat: 32.0853, lng: 34.7818 }, // Example: Tel Aviv
    },
    {
      id: 2,
      name: 'Station B',
      address: '456 Elm St, City B',
      status: 'Occupied',
      waitTime: 3,
      isFavorite: false,
      location: { lat: 31.7683, lng: 35.2137 }, // Example: Jerusalem
    },
  ]);

  const [searchQuery, setSearchQuery] = useState('');
  const [filteredStations, setFilteredStations] = useState(stations);

  const handleSearch = (event) => {
    const query = event.target.value.toLowerCase();
    setSearchQuery(query);
    const filtered = stations.filter(
      (station) =>
        station.name.toLowerCase().includes(query) ||
        station.address.toLowerCase().includes(query)
    );
    setFilteredStations(filtered);
  };

  const mapContainerStyle = {
    width: '100%',
    height: '400px',
    marginTop: '20px',
    borderRadius: '10px',
    border: '2px solid #ccc',
  };

  const mapCenter = stations.length > 0 ? stations[0].location : { lat: 0, lng: 0 };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', padding: '20px' }}>
      <h1 style={{ textAlign: 'center', color: '#4CAF50' }}>Charging Stations</h1>

      {/* Search Bar */}
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Search stations by name or address..."
          value={searchQuery}
          onChange={handleSearch}
          style={{
            width: '100%',
            padding: '12px 15px',
            fontSize: '16px',
            borderRadius: '5px',
            border: '1px solid #ccc',
            boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
          }}
        />
      </div>

      {/* Display list of stations */}
      <div>
        <h2 style={{ color: '#333', marginBottom: '15px' }}>Available Stations</h2>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {filteredStations.map((station) => (
            <li
              key={station.id}
              style={{
                margin: '10px 0',
                border: '1px solid #ccc',
                padding: '15px',
                borderRadius: '10px',
                background: station.status === 'Available' ? '#e8f5e9' : '#ffebee',
                boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
              }}
            >
              <h3 style={{ color: '#4CAF50' }}>{station.name}</h3>
              <p>
                <strong>Address:</strong> {station.address}
              </p>
              <p>
                <strong>Status:</strong>{' '}
                <span
                  style={{
                    color: station.status === 'Available' ? '#4CAF50' : '#f44336',
                  }}
                >
                  {station.status}
                </span>
              </p>
              <p>
                <strong>Wait Time:</strong> {station.waitTime} people waiting
              </p>
            </li>
          ))}
        </ul>
      </div>

      {/* Google Map */}
      <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY">
        <GoogleMap
          mapContainerStyle={mapContainerStyle}
          center={mapCenter}
          zoom={10}
        >
          {filteredStations.map((station) => (
            <Marker key={station.id} position={station.location} />
          ))}
        </GoogleMap>
      </LoadScript>
    </div>
  );
}

export default Home;
